import { Text, HStack, Box, VStack } from "@chakra-ui/react";
import {useState, useEffect} from "react"
import { useWeb3React } from "@web3-react/core";
import axios from 'axios';


function TotalPoint() {
    const [pointArr, setPointArr] = useState([]);
    const {account} = useWeb3React();
    const [loading, setLoading] = useState(false);
    useEffect(() => {
        axios.get("http://localhost:5000/api/userInfo", { params: { id: account } })
            .then(function(response){
                setPointArr(pointArr.concat(response.data.pointA,response.data.pointB,response.data.pointC,response.data.pointD));
            });
    }, []) 

    const sumTotalPoint = () => {
        let sumPoint = 0
        for (let i = 0; i < pointArr.length; i++) {
            sumPoint += pointArr[i]
        }
        return sumPoint
    }

    const sumPoint = sumTotalPoint()

    return (
        <VStack
            justify="space-between"
            p={5}
            m={3}
            backgroundImage="linear-gradient(#532DFB,#868CFF)"
            w="full"
            h="204px"
            borderRadius={15}
        >
            <HStack w="full" justify="space-between">
                <Text color="white" fontWeight="700" fontSize="3xl">
                    총 보유 포인트
                </Text>
                <Box />
            </HStack>
            <HStack w="full" justify="space-between">
                <Box />
                <Text color="white" fontWeight="700" fontSize="5xl">
                    {sumPoint} point
                </Text>
            </HStack>
        </VStack>
    );
}

export default TotalPoint;
